import { Component, OnChanges, OnInit, SimpleChanges } from '@angular/core';
import { ArtistPhotoService } from 'src/app/services/artist-photo/artist-photo.service';
import { ContinueLyricsService } from 'src/app/services/continue-lyrics-service/continue-lyrics.service';
import { GuessArtistService } from 'src/app/services/guess-artist-service/guess-artist.service';
import { ArtistPhotoInterface } from 'src/interfaces/artist-photo.interface';
import { ContinueLyricsInterface } from 'src/interfaces/continue-lyrics.interface';
import { GuessArtistInterface } from 'src/interfaces/guess-artist.interface';

@Component({
  selector: 'app-quiz',
  templateUrl: './quiz.component.html',
  styleUrls: ['./quiz.component.css'],
})
export class QuizComponent implements OnInit, OnChanges {
  question1: ContinueLyricsInterface = {
    o1: '',
    o2: '',
    o3: '',
    song: '',
    solution: '',
    id: 0,
  };

  question2: GuessArtistInterface = {
    o1: '',
    o2: '',
    o3: '',
    song: '',
    solution: '',
    id: 0,
  };

  question3: ArtistPhotoInterface = {
    o1: '',
    o2: '',
    o3: '',
    url: '',
    solution: '',
    id: 0,
  };
  selectedOption1: string = '';
  selectedOption2: string = '';
  selectedOption3: string = '';
  constructor(
    private lyricsService: ContinueLyricsService,
    private guessArtistService: GuessArtistService,
    private artistPhotoService: ArtistPhotoService
  ) {}

  ngOnChanges(changes: SimpleChanges) {}

  ngOnInit(): void {
    this.lyricsService
      .getContinueLyrics()
      .subscribe((data: ContinueLyricsInterface) => {
        this.question1 = data;
      });
    this.guessArtistService
      .getGuessTheArtist()
      .subscribe((data: GuessArtistInterface) => {
        this.question2 = data;
      });
    this.artistPhotoService
      .getArtistPhoto()
      .subscribe((data: ArtistPhotoInterface) => {
        this.question3 = data;
      });
  }
}
<div class="quiz">
    <div class="continueLyrics">
        <label class="questionTypeLabel">Continue the lyrics</label><br>
        <label class="questionLabel">{{question1.song}}</label>

        <div>
            <label>
                <input type="radio" name="option1" [(ngModel)]="selectedOption1" value="option1"> {{question1.o1}}
            </label>
        </div>
        <div>
            <label>
                <input type="radio" name="option1" [(ngModel)]="selectedOption1" value="option2"> {{question1.o2}}
            </label>
        </div>
        <div>
            <label>
                <input type="radio" name="option1" [(ngModel)]="selectedOption1" value="option3"> {{question1.o3}}
            </label>
        </div>
    </div>

    <div class="guessArtist">
        <label class="questionTypeLabel">Guess the Artist: <span class="questionLabel">"{{question2.song}}"</span></label><br>

        <div>
            <label>
                <input type="radio" name="option2" [(ngModel)]="selectedOption2" value="option4"> {{question2.o1}}
            </label>

            <label>
                <input type="radio" name="option2" [(ngModel)]="selectedOption2" value="option5"> {{question2.o2}}
            </label>

            <label>
                <input type="radio" name="option2" [(ngModel)]="selectedOption2" value="option6"> {{question2.o3}}
            </label>
        </div>
    </div>

    <div class="artistPhoto">
        <label class="questionTypeLabel">Recognize the Artist</label><br>
        <div class="questionLayoutContainer">
        <div style="width: 100%; height: 20vh; overflow: hidden;display: flex;flex-direction: column;align-items: center;">
            <img [src]="'../../../../assets/store-photos/'+question3.url" alt="Cropovana slika" style="width: 20vh; height: 20vh; object-fit: cover;margin-bottom: 0;">
        </div>
        <!-- <img [src]="'../../../../assets/store-photos/'+question3.url" alt="img"> -->
        <div>


        <div>
            <label>
                <input type="radio" name="option3" [(ngModel)]="selectedOption3" value="option7"> {{question3.o1}}
            </label>
        </div>
        <div>
            <label>
                <input type="radio" name="option3" [(ngModel)]="selectedOption3" value="option8"> {{question3.o2}}
            </label>
        </div>
        <div>
            <label>
                <input type="radio" name="option3" [(ngModel)]="selectedOption3" value="option9"> {{question3.o3}}
            </label>
        </div>
        </div>
        </div>
    </div>
    <div class="result">
oj
    </div>
</div>
