<div class="container">
    <div class="topList">
        <ul class="list">
            <ng-container *ngFor="let video of trendingVideos; let i = index">
                <li *ngIf="i < 10"><span>{{ i + 1 }}#</span> {{ video.snippet.title }}</li>
            </ng-container>
        </ul>
    </div>

    <div class="mid">
        <div class="welcome">
            <img class="logo" src="../../../assets/mojLogo.png" alt="nesto">
            <img class="title" src="../../../assets/naziv.png" alt="nesto">
            <img class="moto" src="../../../assets/moto.png" alt="nesto">
        </div>

        <div class="getStarted">
            <button class="buttonGetSinging" (click)="goToKaraokeRoom()">GET SINGING</button>
        </div>

        <div class="galery">
            <div class="box">
            <span *ngFor="let i of pictures; let index = index" [style.--i]="index + 1">
                <img class="galeryImage" [src]="'../../../assets/galery/img' + (index + 1) + '.jpg'">
            </span>
            </div>
        </div>

    </div>

    <div class="topList">.
        <ul class="list">
            <ng-container *ngFor="let video of trendingVideos; let i = index">
                <li *ngIf="i >= 10"><span>{{ i + 1 }}#</span> {{ video.snippet.title }}</li>
            </ng-container>
        </ul>
    </div>
</div>
