<input type="text" [(ngModel)]="searchQuery" (keyup.enter)="performSearch()" placeholder="Unesite ime pesme">
<button (click)="performSearch()">Pretraži</button>
</div>

<div>

    <div *ngIf="loading">Učitavanje...</div>
    <div *ngIf="error">Neuspešna pretraga. Pokušajte ponovo.</div>
    <app-search *ngIf="videoId" [videoId]="videoId">
    </app-search>
</div>
