import { Component, EventEmitter, Input, OnChanges, OnInit, Output } from '@angular/core';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';


@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent implements OnInit,OnChanges{
  @Input() videoId: string = '';

  @Output() videoName:string='';

  ngOnInit(): void {
    console.log("ovde sam");
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes.videoId) {
      // Kada se videoId promeni, automatski čuvajte naziv videa
      this.saveVideoName();
    }
  }

  saveVideoName() {
    // Emitovanje naziva videa
    this.videoName = "Naziv videa"; // Postavite stvarni naziv videa
  }

  constructor(private domSanitizer: DomSanitizer)
  {

  }

  playSelectedVideo(videoName: string) {
    // Emitovanje naziva videa
    this.videoNameSelected.emit(videoName);
  }

  get videoUrl() {
    const url = `https://www.youtube.com/embed/${this.videoId}?fs=1&vq=high&modestbranding=1?autoplay=0&rel=0`;
    console.log(url);
    return this.domSanitizer.bypassSecurityTrustResourceUrl(url);
  }
}
