import { Component, OnChanges, OnInit, SimpleChanges } from '@angular/core';
import { ContinueLyricsService } from 'src/app/services/continue-lyrics-service/continue-lyrics.service';
import { ContinueLyricsInterface } from 'src/interfaces/continue-lyrics.interface';

@Component({
  selector: 'app-quiz',
  templateUrl: './quiz.component.html',
  styleUrls: ['./quiz.component.css'],
})
export class QuizComponent implements OnInit, OnChanges {
  question1: ContinueLyricsInterface = {
    o1: '',
    o2: '',
    o3: '',
    song: '',
    solution: '',
    id: 0,
  };
  selectedOption: string = '';
  constructor(private lyricsService: ContinueLyricsService) {}
  ngOnChanges(changes: SimpleChanges): void {
    // this.lyricsService
    //   .getContinueLyrics()
    //   .subscribe((data: ContinueLyricsInterface) => {
    //     this.question1 = data;
    //   });
  }

  ngOnInit(): void {
    this.lyricsService
      .getContinueLyrics()
      .subscribe((data: ContinueLyricsInterface) => {
        this.question1 = data;
      });
  }
}
<div class="quiz">
    <div class="continueLyrics">
        <h1>Continue the lyrics</h1>
        <p>{{question1.song}}</p>
        <div>
            <label>
                <input type="radio" name="option" [(ngModel)]="selectedOption" value="option1"> {{question1.o1}}
            </label>
        </div>
        <div>
            <label>
                <input type="radio" name="option" [(ngModel)]="selectedOption" value="option2"> {{question1.o2}}
            </label>
        </div>
        <div>
            <label>
                <input type="radio" name="option" [(ngModel)]="selectedOption" value="option3"> {{question1.o2}}
            </label>
        </div>
    </div>
