import { Component, OnInit } from '@angular/core';
import { Store, select } from '@ngrx/store';
import * as SearchActions from './you-tube-service/you-tube-store/you-tube-store.actions';
import { AppState } from 'src/app/app.state';
import { selectVideoId } from './you-tube-service/you-tube-store/you-tube-store.selectors';
import { YouTubeService } from './you-tube-service/you-tube.service';
import { addVideoName } from './video-names-store/video-names.actions';
import { selectVideoNames } from './video-names-store/video-names.selector';

@Component({
  selector: 'app-karaoke-room',
  templateUrl: './karaoke-room.component.html',
  styleUrls: ['./karaoke-room.component.css']
})
export class KaraokeRoomComponent implements OnInit{

  dotsArray = new Array(15);
  selectedVideos: string[] = [];
  uniqueVideoNames: Set<string> = new Set();

  constructor(private store: Store<AppState>,private youTubeService: YouTubeService) {}

  searchQuery: string = '';
  localSearchQuery:string='';
  videoId: string = '';
  loading: boolean = false;
  error: boolean = false;

  performSearch() {
    if (this.searchQuery.trim() !== '') {
      this.localSearchQuery=this.searchQuery+" karaoke";
      this.store.dispatch(SearchActions.searchAction({ query: this.localSearchQuery }));
      this.store.select(state => state.videoId).subscribe(searchState => {
        this.videoId = searchState.videoId;
        this.loading = searchState.loading;
        this.error = searchState.error;
      });
    }
  }


  ngOnInit() {
  this.store.select(selectVideoId).subscribe(videoId => {
    this.videoId = videoId;
  });

  this.store.select(state => state.videoId.loading).subscribe(loading => {
    this.loading = loading;
  });

  this.store.select(selectVideoNames).subscribe(videoNamesSet => {
    // Pretvorite Set<string> u niz za prikaz u komponenti
    this.selectedVideos = Array.from(videoNamesSet).slice(-10); // Prikazi samo poslednjih 10 imena
  });
}


  handleVideoNameSelected(videoName: string) {
  this.youTubeService.getVideoNameByName(videoName)
    .then(videoTitle => {
      if (videoTitle) {
        this.uniqueVideoNames.add(videoName);
        this.store.dispatch(addVideoName({ videoName: videoTitle }));
        // Ovde moÅ¾ete dodati logiku za Äuvanje naziva u bazi
      } else {
        console.log('Video sa tim nazivom nije pronaÄ‘en.');
      }
    })
    .catch(error => {
      console.error('GreÅ¡ka prilikom dohvatanja naziva videa:', error);
    });
}






}

<div class="velikiContainer">
<div class="ime" >
<img class="img" src="../../../assets/naziv.png" alt="nesto">

</div>
<div class="divic">
    <form class="inputForm">
        <img style="height: 60%;margin-top: 1.3%; margin-right: 1.3%;margin-left: 1.5%;" src="../../../assets/search.png" alt="">
        <input type="text" [(ngModel)]="searchQuery" name="unos" (keyup.enter)="performSearch()" placeholder="enter the name of the song . . .">
        <button class="searchButton" type="submit" (click)="performSearch()">search</button>


    </form>
</div>
</div>
<button class="backButton" routerLink="/">ðŸ”™</button>

<div style="box-shadow: 20px 20px 30px rgba(0, 0, 0, 0.8);height: 75vh; width: 60vw;margin-left:19vw ;background-image: url('../../../assets/div.png');
    background-size: cover;
    background-repeat: no-repeat;
     padding-top:30px;padding-bottom:30px;">
    <!-- <section *ngIf="loading"> -->

        <div *ngIf="loading" style="margin-left: 30vw; margin-top: 20vh;">
            <div *ngFor="let i of dotsArray; let index = index" class="dots" [style.--i]="index + 1"></div>
        </div>
    <!-- </section> -->
    <div *ngIf="error">No results found. </div>
    <app-search *ngIf="videoId" [videoId]="videoId" (videoNameSelected)="handleVideoNameSelected($event)"></app-search>
</div>

<ul>
    <li *ngFor="let videoName of selectedVideos">{{ videoName }}</li>
</ul>
