import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { ContinueLyricsInterface } from 'src/interfaces/continue-lyrics.interface';

@Injectable({
  providedIn: 'root',
})
export class ContinueLyricsService {
  private apiUrl = 'http://localhost:3000';

  constructor(private http: HttpClient) {}

  getContinueLyrics(): Observable<ContinueLyricsInterface> {
    let randomNumber: number = Math.floor(Math.random() * 4) + 1;
    return this.http
      .get<any[]>(`${this.apiUrl}/continueLyrics/${randomNumber}`)
      .pipe(
        map((data: any[]) => {
          // Ovde možete izvući jedan element iz niza
          if (data.length > 0) {
            return data[0] as ContinueLyricsInterface;
          } else {
            throw new Error('Nema dostupnih podataka.');
          }
        })
      );
  }
}
