import { Component, OnInit } from '@angular/core';
import { NameSharingService } from '../../service/name-sharing.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{

  enteredName: string = '';

  constructor(private nameService: NameSharingService, private router: Router) {}

  goToFocusRoom() {
    if(this.enteredName.length==0)
      this.enteredName='Anonymous';
    this.nameService.setCurrentName(this.enteredName);
    this.router.navigate(['/focus-room']);
  }

  goToClassroom() {
    this.nameService.setCurrentName(this.enteredName);
    this.router.navigate(['/classroom']);
  }

  ngOnInit(): void {

  }

}
